import React, { useEffect, useState } from 'react';
import { connect } from "react-redux";
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux";
import { setProducts } from '../redux/actions/productActions';
import { Typography, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import { QRCodeCanvas } from 'qrcode.react';

const ProductDetail = (props) => {
  const { allProducts } = props;
  const dispatch = useDispatch();
  const { productName, description, productImage, carbohydrateValue, glutenFree, glutenValue, ketogenicDiet, oilValue, proteinValue, saltFree, calorieValue } = allProducts;
  const productId = useParams().id;
  const [product, setProduct] = useState({});
  const [showQRPopup, setShowQRPopup] = useState(false);

  const fetchProducts = async () => {
    try {
      const resp = await axios.get("http://localhost:3001/products");
      if (resp && resp.status === 200) {
        const decodedProducts = resp.data.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        dispatch(setProducts(decodedProducts));
      }
    } catch (error) {
      console.log("Error:", error);
    }
  };

  const getProductId = () => {
    allProducts && allProducts.map((product) => {
      if (productId === product.productId) {
        setProduct(product);
        return;
      }
    });
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    if (Object.keys(product).length === 0) {
      getProductId();
    }
  }, [allProducts]);

  const handleQRButtonClick = () => {
    setShowQRPopup(true);
  };

  const closeQRPopup = () => {
    setShowQRPopup(false);
  };

  return (
    <div>
      <h1 style={{ color: 'white' }}>Product Detail</h1>
      <Paper elevation={10} style={{ marginTop: "50px", marginBottom: "80px", height: "950px" }}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <img src={product.productImage} align="center" style={{ marginBottom: "50px", marginLeft: "10px", width: '100%', height: 'auto' }}></img>
          </Grid>
          <Grid item xs={12} sm={6} style={{ color: "#9c27b0", padding: '20px' }}>
            <Typography variant="h4" component="h2" style={{ marginBottom: '10px' }}>
              {product.productName}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              {product.description}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Protein: {product.proteinValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Hidrat: {product.carbohydrateValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Yağ: {product.oilValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Gluten Değeri: {product.glutenValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Gluten Free: {product.glutenFree}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Ketogenic Diet: {product.ketogenicDiet}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Salt Free: {product.saltFree}
            </Typography>
            <Typography variant="body1" component="p">
              Kalori: {product.calorieValue} cal
            </Typography>
            <Button onClick={handleQRButtonClick} variant="contained" style={{ marginTop: '10px' }}>
              Show QR Code
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* QR Code Popup */}
      <Dialog open={showQRPopup} onClose={closeQRPopup}>
        <DialogTitle>QR Code</DialogTitle>
        <DialogContent>
          <QRCodeCanvas value={productId} />
        </DialogContent>
        <DialogActions>
          <Button onClick={closeQRPopup} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

const mapStateToProps = (state) => ({
  allProducts: state.allProducts.products
});

const mapDispatchToProps = () => ({});

export default connect(mapStateToProps, mapDispatchToProps)(ProductDetail);
