import React, { useState } from 'react';
import { connect } from 'react-redux';
import Grid from '@mui/material/Grid';
import { useNavigate } from 'react-router-dom';
import { Avatar, Button, Paper, TextField, Typography } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Link } from 'react-router-dom';
import { createUser } from '../redux/actions/productActions';
import axios from 'axios';
import Alert from '@mui/material/Alert';

const RegisterPage = (props) => {
  const { createUser, history } = props;
  const navigate = useNavigate();
  const paperStyle = { padding: 20, height: '70vh', width: 600, margin: '120px auto' };
  const avatarStyle = { backgroundColor: '#32e232' };
  const buttonStyle = { margin: '8px 0' };
  const textFieldStyle = { marginBottom: '15px' };

  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [tel, setTel] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [age, setAge] = useState('');
  const [weight, setWeight] = useState('');
  const [height, setHeight] = useState('');
  const [amountOfWater, setAmountOfWater] = useState('');
  const [dailyMovementAmount, setDailyMovementAmount] = useState('');
  const [desiredDestination, setDesiredDestination] = useState('');

  const isFormCompleted =
    name &&
    surname &&
    email &&
    tel &&
    password &&
    age &&
    weight &&
    height &&
    amountOfWater &&
    dailyMovementAmount &&
    desiredDestination;

  const createUserRegister = async () => {
    try {
      const resp = await axios.post('http://localhost:3001/userRegister', {
        uid: '',
        name: name,
        surname: surname,
        email: email,
        tel: tel,
        password: password,
        age: age,
        weight: weight,
        height: height,
        amountOfWater: amountOfWater,
        dailyMovementAmount: dailyMovementAmount,
        desiredDestination: desiredDestination,
      });

      createUser(resp.data);

      // Redirect to the login page after successful registration
      navigate('/loginPage');
    } catch (error) {
      console.log('Error:', error);
      setError('An error occurred during registration.');
    }
  };

  return (
     <Grid container justifyContent="center">
      <Paper elevation={10} style={paperStyle}>
        <Grid container justifyContent="center" alignItems="center">
          <Grid item xs={12} align="center">
            <Avatar style={avatarStyle}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography variant="h4" align="center">
              Welcome! Let's do the registration.
            </Typography>
          </Grid>
          <Grid item xs={12} align="center">
            <Typography variant="h5">User Information</Typography>
          </Grid>
          <Grid item xs={6}>
            {/* İlk kısımın alanları */}
          </Grid>
          <Grid item xs={6}>
            {/* İkinci kısımın alanları */}
          </Grid>
          <Grid item xs={12} align="center">
            <Button
              type="submit"
              color="primary"
              variant="contained"
              disabled={!isFormCompleted}
              fullWidth
              style={buttonStyle}
              onClick={createUserRegister}
            >
              Sign In
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Grid>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    createUser: (user) => dispatch(createUser(user)),
  };
};

export default connect(null, mapDispatchToProps)(RegisterPage);
