import React from 'react'
import { useSelector } from 'react-redux'
import { Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import '@fontsource/roboto';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';

const Product = () => {

    const products = useSelector((state) => state.allProducts.products);
    const renderList = products.map((product) => {

        const { id, title, image, price, category } = product;

        return (
            <Paper elevation={10} >
                <Link to={`/product/${id}`}>
                    <Grid item xs={12} sm={7} style={{ marginBottom: "50px", marginTop: "30px" }} >
                        <img src={image} alt={title} aling="center" style={{ width: "300px", height: "350px", margin: "inherit", marginLeft: "10px" }}></img>
                    </Grid>
                </Link>
                <Grid item xs={12} sm={7} style={{ marginBottom: "50px", marginTop: "30px" }} >
                    <Typography component="h2" variant="h4" >{category}</Typography>
                </Grid>

            </Paper >

        );
    })


    return <>{renderList}</>;

}


export default Product
