import React, { useState } from 'react'
import List from './List'
import ListForm from './ListForm'


const UserList = () => {

    const [lists, setLists] = useState([])
    const [dragSrcEl, setDragSrcEl] = useState([])

    const addUser = list => {
        if (!list.text || /^\s*$/.test(list.text)) {
            return
        }

        const newList = [list, ...lists]

        setLists(newList)
    }

    const updateList = (listId, newValue) => {
        if (!newValue.text || /^\s*$/.test(newValue.text)) {
            return
        }

        setLists(previous => previous.map(item => (item.id === listId ? newValue : item)))

    }

    const removeList = id => {
        const removeArr = [...lists].filter(list => list.id !== id)

        setLists(removeArr);
    }

    const sırala = id => {
        const sıralaArr = [...lists].filter(list => list.id !== id)

        setLists(sırala);
    }


    const completeList = id => {
        let updatedLists = lists.map(list => {
            if (list.id === id) {
                list.isComplete = !list.isComplete
            }
            return list;
        });
        setLists(updatedLists);
    }


    document.addEventListener('DOMContentLoaded', (event) => {

        var dragSrcEl = null;

        function handleDragStart(e) {
          this.style.opacity = '0.4';

          dragSrcEl = this;

          e.dataTransfer.effectAllowed = 'move';
          e.dataTransfer.setData('text/html', this.innerHTML);
        }

        function handleDragOver(e) {
          if (e.preventDefault) {
            e.preventDefault();
          }

          e.dataTransfer.dropEffect = 'move';

          return false;
        }

        function handleDragEnter(e) {
          this.classList.add('over');
        }

        function handleDragLeave(e) {
          this.classList.remove('over');
        }

        function handleDrop(e) {
          if (e.stopPropagation) {
            e.stopPropagation(); // stops the browser from redirecting.
          }

          if (dragSrcEl != this) {
            dragSrcEl.innerHTML = this.innerHTML;
            this.innerHTML = e.dataTransfer.getData('text/html');
          }

          return false;
        }

        function handleDragEnd(e) {
          this.style.opacity = '1';

          items.forEach(function (item) {
            item.classList.remove('over');
          });
        }


        let items = document.querySelectorAll('.container .box');
        items.forEach(function(item) {
          item.addEventListener('dragstart', handleDragStart, false);
          item.addEventListener('dragenter', handleDragEnter, false);
          item.addEventListener('dragover', handleDragOver, false);
          item.addEventListener('dragleave', handleDragLeave, false);
          item.addEventListener('drop', handleDrop, false);
          item.addEventListener('dragend', handleDragEnd, false);
        });
      });

    return (
        <div>
            <ListForm onSubmit={addUser} ></ListForm>
            <List
                lists={lists}
                completeList={completeList}
                removeList={removeList}
                updateList={updateList}
            ></List>
        </div>
    )
}

export default UserList
