import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { setProducts } from '../redux/actions/productActions';
import axios from 'axios';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';

const ProductListing = (props) => {
  const { allProducts } = props;
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(10); // Her sayfada kaç ürün gösterileceği

  const fetchProducts = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/products?page=${currentPage}&limit=${productsPerPage}`);
      if (resp && resp.status === 200) {
        const { pagination, products } = resp.data;
        const { totalPages } = pagination;

        const decodedProducts = products.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        props.setProducts(decodedProducts);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  const handleAddToList = async (product) => {
    try {
      const response = await axios.post('http://localhost:3001/addList', {
        calorieListId: '12345678', // Değerleri isteğinize göre ayarlayın
        products: [
          {
            productId: product.productId, // Tıklanan ürünün ID'sini kullanın
            productName: product.productName, // Tıklanan ürünün diğer bilgilerini isteğinize göre ayarlayın
            description: product.description,
            productImage: product.productImage,
            proteinValue: product.proteinValue,
            carbohydrateValue:product.carbohydrateValue,
            oilValue:product.oilValue,
            glutenValue: product.glutenValue,
            ketogenicDiet:product.ketogenicDiet,
            glutenFree: product.glutenFree,
            saltFree: product.saltFree,
            calorieValue: product.calorieValue,
          },
        ],
        totalCalorie: '150',
        createDate: '2023-06-03',
      });
      console.log(response.data); // Yanıtı konsola yazdırabilirsiniz
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [currentPage]);

  const renderList = () => {
    if (!allProducts) {
      return null;
    }

    if (allProducts.length === 0) {
      return <p>No products found.</p>;
    }

    return allProducts.map((product) => {
      const { productId, productName, description, productImage, carbohydrateValue, glutenFree, glutenValue, ketogenicDiet, oilValue, proteinValue, saltFree, calorieValue } = product;

      return (
        <Link to={`/product/${productId}`} style={{ textDecoration: 'none', color: '#9c27b0' }} key={productId}>
          <Paper elevation={10} style={{ marginTop: '50px', marginBottom: '80px', padding: '20px', position: 'relative' }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={4}>
                <img
                  src={productImage}
                  alt={description}
                  align="center"
                  style={{ width: '100%', height: 'auto' }}
                />
              </Grid>
              <Grid item xs={12} sm={8}>
                <Typography variant="h4" component="h2" style={{ marginBottom: '10px' }}>
                  {productName}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  {description}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Protein: {proteinValue}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Hidrat: {carbohydrateValue}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Yağ: {oilValue}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Gluten Değeri: {glutenValue}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Gluten Free: {glutenFree}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Ketogenic Diet: {ketogenicDiet}
                </Typography>
                <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                  Salt Free: {saltFree}
                </Typography>
                <Typography variant="body1" component="p">
                  Kalori: {calorieValue}
                </Typography>
                <Button
                  variant="contained"
                  color="secondary"
                  size="small"
                  style={{ position: 'absolute', bottom: '10px', right: '10px' }}
                  onClick={() => handleAddToList(product)}
                >
                  Bugünün Listesine Ekle
                </Button>
              </Grid>
            </Grid>
          </Paper>
        </Link>
      );
    });
  };

  const handlePageChange = (event, page) => {
    setCurrentPage(page);
  };

  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = allProducts.slice(indexOfFirstProduct, indexOfLastProduct);

  const pageNumbers = Math.ceil(allProducts.length / productsPerPage);

  return (
    <div>
      <h1 style={{ color: '#9c27b0' }}>Product Listing</h1>
      {renderList()}
      <Stack spacing={2} style={{ marginTop: '20px' }}>
        <Pagination count={8} color="secondary" page={currentPage} onChange={handlePageChange} />
      </Stack>
    </div>
  );
};

const mapStateToProps = (state) => ({
  allProducts: state.allProducts.products
});

const mapDispatchToProps = {
  setProducts
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductListing);
