import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { setProducts } from '../redux/actions/productActions';
import axios from 'axios';
import { setCategoryList } from '../redux/actions/productActions';

const CategoryList = (props) => {
  const { allProducts, calorieInfoList, createCalorieInfo,setCategoryList ,setProducts} = props;
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(10); 

  const fetchProducts = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/products?page=${currentPage}&limit=${productsPerPage}`);
      if (resp && resp.status === 200) {
        const { pagination, products } = resp.data;

        const decodedProducts = products.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        setProducts(decodedProducts);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };


  const fetchCategoryList = async () => {
    try {
      const resp = await axios.get('http://localhost:3001/dietCategories');
      if (resp && resp.status === 200) { // Response body'sini konsola yazdır
        setCategoryList(resp.data);
        console.log('Category List:', resp.data);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };
  

  useEffect(() => {
    fetchProducts();
  }, [currentPage]);

  useEffect(() => {
    fetchCategoryList();
  }, []);

  return (
    <div>
      <h2>Category List:</h2>
      {categoryList.map((category) => (
        <div key={category.id}>{category.name}</div>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  allProducts: state.allProducts.products,
});

const mapDispatchToProps = {
  setProducts,
};

export default connect(mapStateToProps, mapDispatchToProps)(CategoryList);
