import React, { useState } from 'react';
import { connect } from 'react-redux';
import Grid from '@mui/material/Grid';
import { useNavigate } from 'react-router-dom';
import { Avatar, Button, Paper, TextField, Typography } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Link } from 'react-router-dom';
import { createUser } from '../redux/actions/productActions';
import axios from 'axios';
import Alert from '@mui/material/Alert';

const RegisterPage = (props) => {
  const { createUser, history } = props;
  const navigate = useNavigate();
  const paperStyle = { padding: 20, height: '70vh', width: 320, margin: '120px auto' };
  const avatarStyle = { backgroundColor: '#32e232' };
  const buttonStyle = { margin: '8px 0' };
  const textFieldStyle = { marginBottom: '15px' };

  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [tel, setTel] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [age, setAge] = useState('');
  const [kilo, setKilo] = useState('');
  const [height, setHeight] = useState('');
  const [amountofWater, setAmountofWater] = useState('');
  const [dailyMovementAmount, setDailyMovementAmount] = useState('');
  const [desiredDestination, setDesiredDestination] = useState('');
  const [isFormCompleted, setIsFormCompleted] = useState(false);

  const createUserRegister = async () => {
    try {
      const resp = await axios.post('http://localhost:3001/userRegister', {
        uid: '',
        name: name,
        surname: surname,
        email: email,
        tel: tel,
        password: password,
        age: age,
        kilo: kilo,
        height: height,
        amountofWater: amountofWater,
        dailyMovementAmount: dailyMovementAmount,
        desiredDestination: desiredDestination,
      });

      createUser(resp.data);

      // Başarılı kayıt olduğunda login sayfasına yönlendirme
      navigate('/loginPage');
    } catch (error) {
      console.log('Error:', error);
      setError('An error occurred during registration.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case 'name':
        setName(value);
        break;
      case 'surname':
        setSurname(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'tel':
        setTel(value);
        break;
      case 'password':
        setPassword(value);
        break;
      case 'age':
        setAge(value);
        break;
      case 'kilo':
        setKilo(value);
        break;
      case 'height':
        setHeight(value);
        break;
      case 'amountofWater':
        setAmountofWater(value);
        break;
      case 'dailyMovementAmount':
        setDailyMovementAmount(value);
        break;
      case 'desiredDestination':
        setDesiredDestination(value);
        break;
      default:
        break;
    }
  };

  const handleFormSubmit = () => {
    if (name && surname && email && tel && password && age && kilo && height && amountofWater && dailyMovementAmount && desiredDestination) {
      setIsFormCompleted(true);
    }
  };

  return (
    <Grid>
      <Paper elevation={10} style={paperStyle}>
        <Grid align="center">
          <Avatar style={avatarStyle}>
            <LockOutlinedIcon />
          </Avatar>
          <h2>Register</h2>
        </Grid>
        <TextField
          label="Name"
          placeholder="Enter name"
          fullWidth
          required
          style={textFieldStyle}
          color="secondary"
          name="name"
          value={name}
          onChange={handleInputChange}
        />
        <TextField
          label="Surname"
          placeholder="Enter surname"
          fullWidth
          required
          style={textFieldStyle}
          color="secondary"
          name="surname"
          value={surname}
          onChange={handleInputChange}
        />
        <TextField
          label="Email"
          placeholder="Enter email"
          fullWidth
          required
          style={textFieldStyle}
          color="secondary"
          name="email"
          value={email}
          onChange={handleInputChange}
        />
        <TextField
          label="Phone number"
          placeholder="Enter phone number"
          fullWidth
          required
          style={textFieldStyle}
          color="secondary"
          name="tel"
          value={tel}
          onChange={handleInputChange}
        />
        <TextField
          label="Password"
          placeholder="Enter password"
          type="password"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="password"
          value={password}
          onChange={handleInputChange}
        />
        <Typography style={{ marginTop: '10px', marginBottom: '5px' }}>Seni Biraz Tanıyalım</Typography>
        <TextField
          label="Age"
          placeholder="Enter your Age"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="age"
          value={age}
          onChange={handleInputChange}
        />
        <TextField
          label="Kilo"
          placeholder="Enter your kilo"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="kilo"
          value={kilo}
          onChange={handleInputChange}
        />
        <TextField
          label="Height"
          placeholder="Enter your height"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="height"
          value={height}
          onChange={handleInputChange}
        />
        <TextField
          label="Amount of Water"
          placeholder="Enter your daily water consumption amount"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="amountofWater"
          value={amountofWater}
          onChange={handleInputChange}
        />
        <TextField
          label="Daily Movement Amount"
          placeholder="Enter your Daily Movement Amount"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="dailyMovementAmount"
          value={dailyMovementAmount}
          onChange={handleInputChange}
        />
        <TextField
          label="Desired Destination"
          placeholder="Specify your Desired Destination"
          style={textFieldStyle}
          fullWidth
          required
          color="secondary"
          name="desiredDestination"
          value={desiredDestination}
          onChange={handleInputChange}
        />
        <FormControlLabel control={<Checkbox defaultChecked color="secondary" />} label="Remember me" />
        {error && (
          <Alert severity="error" onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        <Button
          type="submit"
          color="secondary"
          variant="contained"
          fullWidth
          style={buttonStyle}
          onClick={createUserRegister}
          disabled={!isFormCompleted}
        >
          SIGN IN
        </Button>
        <Typography>
          Do you have an account?
          <Link to="/loginPage" color="secondary">
            Sign IN
          </Link>
        </Typography>
      </Paper>
    </Grid>
  );
};

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {
  createUser,
};

export default connect(mapStateToProps, mapDispatchToProps)(RegisterPage);
