import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { setCalorieInfoList, setProducts } from '../redux/actions/productActions';
import axios from 'axios';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';

const CalorieInfoPage = (props) => {
  const { allProducts, calorieInfoList } = props;
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(10); // Her sayfada kaç ürün gösterileceği

  const fetchProducts = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/products?page=${currentPage}&limit=${productsPerPage}`);
      if (resp && resp.status === 200) {
        const { pagination, products } = resp.data;
        const { totalPages } = pagination;

        const decodedProducts = products.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        props.setProducts(decodedProducts);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  const calculateTotalCalories = () => {
    // İlgili tarih bilgisini alın
    const currentDate = calorieInfoList; // Diyelim ki createDate formatı "YYYY-MM-DD"
  
    // Aynı tarihte eklenen ürünleri filtrele
    const sameDateProducts = calorieInfoList.filter(
      (product) => product === currentDate
    );
  
    // Toplam kalori değerini hesapla
    const totalCalories = sameDateProducts.reduce(
      (total, product) => total + product.calorieValue,
      0
    );
  
    return totalCalories;
  };
  

  const fetchCalorieInfoList = async () => { // Ekledim
    try {
      const resp = await axios.get(`http://localhost:3001/calorieInfo`);
      if (resp && resp.status === 200) {
        props.setCalorieInfoList(resp.data);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
    fetchCalorieInfoList(); // Ekledim
  }, [currentPage]);

  useEffect(() => {
    fetchCalorieInfoList(); // Ekledim
  }, []);

  // Şu anki sayfadaki ürünleri al
  const indexOfLastProduct = currentPage * productsPerPage;

  // Sayfa değişikliğini işle
  const handlePageChange = (event, page) => {
    setCurrentPage(page);
  };

  return (
    <div>
      <h1 style={{ color: '#9c27b0' }}>Calorie Info Page</h1>
      <div>
        <h2>Total Calories: {calculateTotalCalories()}</h2>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  allProducts: state.allProducts.products,
  calorieInfoList: state.calorieInfo.products // Ekledim
});

const mapDispatchToProps = {
  setProducts,
  setCalorieInfoList // Ekledim
};

export default connect(mapStateToProps, mapDispatchToProps)(CalorieInfoPage);
