import React, { useEffect, useState } from 'react';
import { styled } from '@mui/system';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import axios from 'axios';
import { connect } from 'react-redux';
import { Grid, Button, Accordion, AccordionSummary, AccordionDetails } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { setCategoryList } from '../redux/actions/productActions';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';

import MenuListComposition from './MenuListComposition';

const Header = (props) => {
  const { categoryList, setCategoryList } = props;
  const [expanded, setExpanded] = useState(false);

  const handleAccordionChange = () => {
    setExpanded(!expanded);
  };

  const fetchCategoryList = async () => {
    try {
      const resp = await axios.get('http://localhost:3001/dietCategories');
      if (resp && resp.status === 200) {
        console.log('Response:', resp.data);
        setCategoryList(resp.data);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchCategoryList();
  }, []);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" style={{ backgroundColor: "rgb(48 195 48)" }}>
        <Toolbar>
          <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexGrow: 1 }}>
            <Button color="inherit" href="/">Product List</Button>
            <Button color="inherit" href="/calorieInformation">Calorie Information Page</Button>
            <MenuListComposition />
          </Box>
          <Button color="inherit" href="mainLoginPage">Login</Button>
          <Button color="inherit" href="registerLoginPage">Register</Button>
        </Toolbar>
        <Accordion expanded={expanded} onChange={handleAccordionChange} style={{ backgroundColor: "white", boxShadow: "none" }}>
          <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
            <ListItemText primary="Category List" />
          </AccordionSummary>
          <AccordionDetails>
            <List>
              {categoryList &&
                categoryList.map((category) => (
                  <ListItem
                    key={category.categoryId}
                    button
                    component="a"
                    href={`/category/${category.categoryId}`}
                  >
                    <ListItemText primary={category.categoryName} />
                  </ListItem>
                ))}
            </List>
          </AccordionDetails>
        </Accordion>
      </AppBar>
    </Box>
  );
}

const mapStateToProps = (state) => ({
  categoryList: state.categoryList.categoryList,
});

const mapDispatchToProps = {
  setCategoryList,
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
