import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { setUserInfo } from '../redux/actions/productActions';

const HomePage = (props) => {
  const { setUserInfo, userSignIn } = props;

 // Token'ı yerel depolamada saklama
localStorage.setItem('token', userSignIn);

// Token'ı yerel depolamadan alma
const token = localStorage.getItem('token');

// Tokenı Axios isteğiyle kullanma
const fetchUserInfo = async () => {
  try {
    const resp = await axios.get('http://localhost:3001/profile', {
      headers: {
        Authorization: `Bearer ${token}` // Tokenı Authorization başlığına ekle
      }
    });

    setUserInfo(resp.data);
  } catch (error) {
    console.log('Error:', error);
  }
};

// Tarayıcı önbelleğini temizleme
localStorage.removeItem('token');

  useEffect(() => {
    fetchUserProfile();
  }, [userSignIn]);

  return (
    <div>Home Page</div>
  );
};

const mapStateToProps = (state) => ({
  userSignIn: state.userSignIn.userSignIn.token,
});

const mapDispatchToProps = {
  setUserInfo,
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
