import React, { useEffect, useState } from 'react';
import { styled } from '@mui/system';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/Inbox';
import MailIcon from '@mui/icons-material/Mail';
import { Grid, Button, Accordion, AccordionSummary, AccordionDetails } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: 240,
  },
}));

const NavigationBar = (props) => {
  const { categoryList } = props;
  const [expanded, setExpanded] = useState('');

  const handleAccordionChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : '');
  };

  return (
    <StyledDrawer variant="permanent" anchor="left">
      <Grid container direction="column" justifyContent="flex-start" alignItems="center" spacing={2}>
        <Grid item>
          <Button>
            <ListItemIcon>
            </ListItemIcon>
            <ListItemText primary="Inbox" />
          </Button>
        </Grid>
        <Grid item>
          <Button>
            <ListItemIcon>
            </ListItemIcon>
            <ListItemText primary="Mail" />
          </Button>
        </Grid>
        <Grid item>
          <Button color="inherit" href="/">
            Product List
          </Button>
        </Grid>
        <Grid item>
          <Button color="inherit" href="/calorieInformation">
            Calorie Information Page
          </Button>
        </Grid>
        <Grid item>
          <Accordion expanded={expanded === 'category'}>
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              aria-controls="category-content"
              id="category-header"
              onClick={handleAccordionChange('category')}
            >
              <ListItemText primary="Category List" />
            </AccordionSummary>
            <AccordionDetails>
              <List>
                {categoryList &&
                  categoryList.map((category) => (
                    <ListItem key={category.categoryId} button component="a" href={`/category/${category.categoryId}`}>
                      <ListItemText primary={category.categoryName} />
                    </ListItem>
                  ))}
              </List>
            </AccordionDetails>
          </Accordion>
        </Grid>
      </Grid>
    </StyledDrawer>
  );
};

export default NavigationBar;
