import { CREATE_USER,FETCH_PRODUCTS } from "./types";
import { getProduct } from "../api/user";

const apiBaseUrl = "http://localhost:3000";



export const fetchProducts = (apiBaseUrl) => async (
    dispatch
) => {
    const res = await getProduct(
        apiBaseUrl,
    );
    if (res)
        dispatch({
            type: FETCH_PRODUCTS,
            payload: res.data,
        })
}
