import React, { useEffect, useState } from 'react';
import { connect, useDispatch } from 'react-redux';
import axios from 'axios';
import {  setSelectedNutritionistList } from '../redux/actions/productActions';
import { Card, CardContent, Typography, CircularProgress, Button } from '@mui/material';

const SelectedNutritionistList = (props) => {
    const { setSelectedNutritionistList,selectedNutritionistList} = props;
    const [isLoading, setIsLoading] = useState(true);

    const fetchSelectedNutritionistList = async () => {
        try {
            const resp = await axios.get('http://localhost:3001/nutritionistList');
            setSelectedNutritionistList(resp.data);
        } catch (error) {
            console.log('Error:', error);
        } finally {
            setIsLoading(false);
        }
    };

      
    useEffect(() => {
        fetchSelectedNutritionistList();
    }, []);

    if (isLoading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <CircularProgress color='secondary' />
            </div>
        );
    }

    console.log(selectedNutritionistList &&
        selectedNutritionistList.map((nutritionist) => (console.log(nutritionist.uid))))

    return (
        <div>
        {selectedNutritionistList &&
                selectedNutritionistList.map((nutritionist) => (
                    <div key={nutritionist.id} style={{ color: '#9c27b0' }}>
                        <h2 style={{ color: 'white' }}>{nutritionist.name} {nutritionist.surname}</h2>
                        <Card variant="outlined">
                            <CardContent>
                                <Typography variant="body2" color="secondary">
                                    Age: {nutritionist.age}
                                </Typography>
                                <Typography variant="body2" color="secondary">
                                    University: {nutritionist.uni}
                                </Typography>
                                <Typography variant="body2" color="secondary">
                                    Experience: {nutritionist.experience}
                                </Typography>
                                <Typography variant="body2" color="secondary">
                                    Profession: {nutritionist.profession}
                                </Typography>
                                <Typography variant="body2" color="secondary">
                                    Explanation: {nutritionist.explanation}
                                </Typography>
                                <div style={{ display: 'flex', justifyContent: 'flex-end',marginTop: '10px' }}>
                                    <Button
                                        variant="contained"
                                        color="secondary"
                                        size="small"
                                        onClick={() => handleAddToList(nutritionist.email)}
                                    >
                                        Add to My Dietitians List
                                    </Button>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                ))} 
        </div>
    );
};

const mapStateToProps = (state) => ({
    selectedNutritionistList: state.selectedNutritionistList.nutritionist
});

const mapDispatchToProps = {
    setSelectedNutritionistList
};

export default connect(mapStateToProps, mapDispatchToProps)(SelectedNutritionistList);
