import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { setUserInfo } from '../redux/actions/productActions';

const HomePage = (props) => {
    const { setUserInfo, token, userSignIn} = props;

    const fetchUserInfo = async () => {
        try {
            const resp = await axios.get('http://localhost:3001/profile', {
                headers: {
                    Authorization: `Bearer ${userSignIn}` // Tokenı Authorization başlığına ekle
                }
            });

            setUserInfo(resp.data);
        } catch (error) {
            console.log('Error:', error);
        }
    };

    console.log(userSignIn, "userSignIn ")

    useEffect(() => {
        fetchUserInfo();
    }, [token]);

    return (
        <div>Home Page</div>
    );
};

const mapStateToProps = (state) => ({

    userSignIn: state.userSignIn.token,
    token: state.userInfoToken, // Token durumunu alın
});

const mapDispatchToProps = {
    setUserInfo,
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
