import React, { useEffect, useState } from 'react'
import { connect } from "react-redux";
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux"
import '@fontsource/roboto';
import { setProducts } from '../redux/actions/productActions';

const CalorieInfoPage = (props) => {

    const dispatch = useDispatch();


    const fetchProducts = async () => {
      try {
        const resp = await axios.get("http://localhost:3001/products");
        if (resp && resp.status === 200) {
          const decodedProducts = resp.data.map((product) => {
            const base64Image = `data:image/jpeg;base64,${product.productImage}`;
            return {
              ...product,
              productImage: base64Image
            };
          });
          
          
          dispatch(setProducts(decodedProducts));
        }
      } catch (error) {
        console.log("Error:", error);
      }
    };
  
useEffect(() => {
      fetchProducts();
    }, []);
  

    return (
        <div>
            calorie information page
        </div>
    )
}


const mapStateToProps = (state) => ({
});

const mapDispatchToProps = () => ({});

export default connect()(CalorieInfoPage);
