import jwt from 'jsonwebtoken';
import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { setUserInfo } from '../redux/actions/productActions';

const HomePage = (props) => {
  const { setUserInfo, userSignIn } = props;

  const fetchUserInfo = async () => {
    try {
      const resp = await axios.get('http://localhost:3001/profile', {
        headers: {
          Authorization: `eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySUQiOiI0NGJkYmMyMyJ9.bh1Nw_69SwxLuf3ssnjkgm6hCS4TTaGk2uBoS_K1g6w`, // JWT Bearer tipine uygun Authorization başlığı
        },
      });

      return resp.data;
    } catch (error) {
      console.log('Error:', error);
      throw error;
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div>
      <h1>Welcome to the Home Page</h1>
    </div>
  );
};

const mapStateToProps = (state) => ({
  userSignIn: state.userSignIn.userSignIn.token,
  userInfoToken: state.userInfoToken.userInfoToken,
});

const mapDispatchToProps = {
  setUserInfo,
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
