import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { setProducts } from '../redux/actions/productActions';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { Typography, Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
import { QRCodeCanvas } from 'qrcode.react';

const ProductDetail = ({ allProducts }) => {
  const dispatch = useDispatch();
  const { productId } = useParams();
  const [product, setProduct] = useState({});
  const [showQRPopup, setShowQRPopup] = useState(false);

  const fetchProducts = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/products`);
      if (resp && resp.status === 200) {
        const { pagination, products } = resp.data;
        const { totalPages } = pagination;

        const decodedProducts = products.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        props.setProducts(decodedProducts);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    const getProductId = () => {
      const selectedProduct = allProducts.find((product) => product.productId === productId);
      if (selectedProduct) {
        setProduct(selectedProduct);
      }
    };

    if (allProducts && allProducts.length > 0) {
      getProductId();
    }
  }, [allProducts, productId]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleQRButtonClick = () => {
    setShowQRPopup(true);
  };

  const closeQRPopup = () => {
    setShowQRPopup(false);
  };

  const qrCodeValue = `http://localhost:3000/product/${productId}?name=${product.productName}&description=${product.description}&protein=${product.proteinValue}&carbohydrate=${product.carbohydrateValue}&fat=${product.oilValue}&gluten=${product.glutenValue}&glutenFree=${product.glutenFree}&ketogenic=${product.ketogenicDiet}&saltFree=${product.saltFree}&calories=${product.calorieValue}`;

  return (
    <div>
      <h1 style={{ color: 'white' }}>Product Detail</h1>
      <Paper elevation={10} style={{ marginTop: '50px', marginBottom: '80px', height: '950px' }}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <img src={product.productImage} align="center" style={{ marginBottom: '50px', marginLeft: '10px', width: '100%', height: 'auto' }} alt="Product" />
          </Grid>
          <Grid item xs={12} sm={6} style={{ color: '#9c27b0', padding: '20px' }}>
            <Typography variant="h4" component="h2" style={{ marginBottom: '10px' }}>
              {product.productName}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              {product.description}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Protein: {product.proteinValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Carbohydrate: {product.carbohydrateValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Fat: {product.oilValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Gluten Value: {product.glutenValue}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Gluten Free: {product.glutenFree}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Ketogenic Diet: {product.ketogenicDiet}
            </Typography>
            <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
              Salt Free: {product.saltFree}
            </Typography>
            <Typography variant="body1" component="p">
              Calories: {product.calorieValue} cal
            </Typography>
            <Button
              variant="contained"
              color="secondary"
              size="small"
              onClick={handleQRButtonClick}>
              Show QR Code
            </Button>
            <Dialog open={showQRPopup} onClose={closeQRPopup}>
              <DialogTitle>QR Code</DialogTitle>
              <DialogContent>
                <QRCodeCanvas value={qrCodeValue} />
              </DialogContent>
              <DialogActions>
                <Button onClick={closeQRPopup} color="primary">
                  Close
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
};

const mapStateToProps = (state) => ({
  allProducts: state.allProducts.products,
});

export default connect(mapStateToProps, { setProducts })(ProductDetail);
