import React,{useState,useEffect} from 'react'
import { connect } from 'react-redux';
import Grid from '@mui/material/Grid';
import { Avatar, Button, Paper, TextField, Typography } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Link } from 'react-router-dom';
import { createSignIn } from '../redux/actions/productActions';
import axios from 'axios';
import Alert from '@mui/material/Alert';


const LoginPage = (props) => {

    const {createSignIn} = props;
    const paperStyle = { padding: 20, height: '70vh', width: 320, margin: "120px auto" }
    const avatarStyle = { backgroundColor: '#32e232' }
    const buttonStyle = { margin: "8px 0" }
    const textFieldStyle = { marginBottom: "15px" }
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const createUserSıgnIn = async () => {
        try {
            const resp = await axios.post('http://localhost:3001/signin', {
                email: email,
                password: password,
              }, {
                maxRedirects: 0, // Otomatik yönlendirmeyi devre dışı bırakır
              });
              

            createSignIn(resp.data);
            navigate('/');
        } catch (error) {
            console.log('Error:', error);
            setError('Your e-mail address is not registered in our system. Please complete your registration.');
        }
    };

    return (
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align="center">
                    <Avatar style={avatarStyle}>
                        <LockOutlinedIcon></LockOutlinedIcon>
                    </Avatar>
                    <h2>Sign in</h2>
                </Grid>
                <TextField
                    label="Email"
                    placeholder="Enter email"
                    fullWidth
                    required
                    style={textFieldStyle}
                    color="secondary"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <TextField
                    label="Password"
                    placeholder="Enter password"
                    type="password"
                    style={textFieldStyle}
                    fullWidth
                    required
                    color="secondary"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <FormControlLabel control={<Checkbox defaultChecked color="secondary" />} label="Remember me" />
                {error && (
                    <Alert severity="error" onClose={() => setError('')}>
                        {error}
                    </Alert>
                )}
                <Button
                    type="submit"
                    color="secondary"
                    variant="contained"
                    fullWidth
                    style={buttonStyle}
                    onClick={createUserSıgnIn}
                >
                    SIGN IN
                </Button>
                <Typography>
                    <Link to="/" color='secondary' >
                        Forgot Password ?
                    </Link>
                </Typography>
                <Typography>
                    Do you have an account ?
                    <Link to="/registerPage" color='secondary' >
                        Sign Up
                    </Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {
    createSignIn
};

export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
