import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useSelector, useDispatch } from 'react-redux';
import { Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import '@fontsource/roboto';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { fetchProducts } from '../actions/productAction';

const apiBaseUrl = "http://localhost:3000";

const Product = (props) => {
    const products = useSelector((state) => state.allProducts.products);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchProducts(apiBaseUrl));
    }, [dispatch]);

    const renderList = products.map((product) => {
        const { id, title, image, price, category } = product;

        return (
            <Link to={`/product/${id}`} style={{ color: "#9c27b0" }}>
                <Paper elevation={10} style={{ marginTop: "50px", marginBottom: "80px" }}>
                    <Grid container spacing={2}>
                        <Grid item xs={12} sm={4}>
                            <img src={image} alt={title} align="center" style={{ width: "300px", height: "350px", marginBottom: "50px", marginLeft: "10px" }} />
                        </Grid>
                        <Grid item xs={12} sm={4} style={{ color: "#9c27b0" }}>
                            <Typography variant="h4" component="h2">{category}  </Typography>
                            <Typography variant="h4" component="h2">{price}  </Typography>
                        </Grid>
                    </Grid>
                </Paper>
            </Link>
        );
    });

    return <>{renderList}</>;
}

export default connect(null, null)(Product);
