import React, { useState } from 'react'
import ListForm from './ListForm'
import { FaEdit } from 'react-icons/fa'
import { AiOutlineCloseSquare } from 'react-icons/ai'
import { CgScrollV } from 'react-icons/cg'
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';


const List = ({ lists, completeList, removeList, updateList }) => {

    const [edit, setEdit] = useState({
        id: null,
        value: ''
    })

    const submitUpdate = value => {
        updateList(edit.id, value)
        setEdit({
            id: null,
            value: ''
        })
    }

    // const handleRLDDChange = (newItems) => {
    //     this.setState({ items: newItems });
    //   }

    if (edit.id) {
        return <ListForm edit={edit} onSubmit={submitUpdate} ></ListForm>
    }

    return lists.map((list, index, item) => (
        <div className={list.isComplete ? 'list-row complete' : 'list-row'} key={index}>
            <div key={list.id} onClick={() => completeList(list.id)}>
                {list.text}
            </div>
            <div className="icons">
                <AiOutlineCloseSquare
                    onClick={() => removeList(list.id)}
                    className='delete-icon'
                ></AiOutlineCloseSquare>
                <FaEdit
                    onClick={() => setEdit({ id: list.id, value: list.text })}
                    className='edit-icon'
                >
