import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Grid from '@mui/material/Grid';
import { useNavigate } from 'react-router-dom';
import { Avatar, Button, Paper, TextField, Typography, InputAdornment, IconButton } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Link } from 'react-router-dom';
import { createUser } from '../../redux/actions/productActions';
import axios from 'axios';
import Alert from '@mui/material/Alert';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';

const RegisterPage = (props) => {
  const { createUser, history } = props;
  const navigate = useNavigate();
  const paperStyle = { padding: 20, height: '70vh', width: 600, margin: '120px auto' };
  const avatarStyle = { backgroundColor: '#32e232' };
  const buttonStyle = { margin: '8px 0' };
  const textFieldStyle = { marginBottom: '15px' };

  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [tel, setTel] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [age, setAge] = useState('');
  const [kilo, setKilo] = useState('');
  const [height, setHeight] = useState('');
  const [amountOfWater, setAmountOfWater] = useState('');
  const [dailyMovementAmount, setDailyMovementAmount] = useState('');
  const [desiredDestination, setDesiredDestination] = useState('');
  const [desiredWeight, setDesiredWeight] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const isFormCompleted =
    name &&
    surname &&
    email &&
    tel &&
    password &&
    age &&
    kilo &&
    height &&
    amountOfWater &&
    dailyMovementAmount &&
    desiredWeight &&
    desiredDestination;

  const createUserRegister = async () => {
    try {
      const resp = await axios.post('http://localhost:3001/userRegister', {
        uid: '',
        name: name,
        surname: surname,
        email: email,
        tel: tel,
        password: password,
        age: age,
        kilo: kilo,
        height: height,
        amountOfWater: amountOfWater,
        dailyMovementAmount: dailyMovementAmount,
        desiredWeight: desiredWeight,
        desiredDestination: desiredDestination,
      });

      if (resp.status === 200) {
        // Registration successful
        const user = resp.data;
        createUser(user);

        // Save user to localStorage
        localStorage.setItem('user', JSON.stringify(user));

        // Redirect to the login page after successful registration
        setRegistrationSuccess(true);
        setError('');
      } else {
        // Registration failed
        setError('An error occurred during registration. Please try again.');
      }
    } catch (error) {
      console.log('Error:', error);
      setError('An error occurred during registration. Please try again.');
    }
  };

  useEffect(() => {
    const userFromLocalStorage = localStorage.getItem('user');
    if (userFromLocalStorage) {
      const parsedUser = JSON.parse(userFromLocalStorage);
      createUser(parsedUser);
    }
  }, []);

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  return (
    <Grid container justifyContent="center">
      <Paper elevation={10} style={paperStyle}>
        <Avatar style={avatarStyle}>
          <LockOutlinedIcon />
        </Avatar>
        <h2 align="center">Welcome! Let's do the registration.</h2>
        <Grid container direction="row" spacing={2}>
          <Grid item xs={6}>
            <Grid align="center">
              <Typography variant="h5">User Information</Typography>
            </Grid>
            {/* ... diğer TextField bileşenleri ... */}
          </Grid>
          <Grid item xs={6}>
            <Grid align="center">
              <Typography variant="h5">Let's Get to Know You</Typography>
            </Grid>
            {/* ... diğer TextField bileşenleri ... */}
          </Grid>
        </Grid>
        <Button
          type="submit"
          color="secondary"
          variant="contained"
          disabled={!isFormCompleted}
          fullWidth
          style={buttonStyle}
          onClick={createUserRegister}
        >
          Register
        </Button>

        {error && (
          <Alert severity="error" style={textFieldStyle}>
            {error}
          </Alert>
        )}

        {registrationSuccess && (
          <div>
            <Typography variant="body1" align="center">
              Your registration has been successful.
            </Typography>
            <Button
              variant="contained"
              color="secondary"
              fullWidth
              style={buttonStyle}
              onClick={() => navigate('/loginPage')}
            >
              Sign In
            </Button>
          </div>
        )}
      </Paper>
    </Grid>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    createUser: (user) => dispatch(createUser(user)),
  };
};

export default connect(null, mapDispatchToProps)(RegisterPage);
