import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { createCalorieInfo, setCalorieInfoList, setProducts } from '../redux/actions/productActions';
import axios from 'axios';
import { CircularProgress, Grid, Paper, Typography, Button, TextField } from '@mui/material';

const CalorieInfoPage = (props) => {
  const { allProducts, calorieInfoList, createCalorieInfo } = props;
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(10); // Her sayfada kaç ürün gösterileceği
  const [totalCalories, setTotalCalories] = useState(0); // Toplam kalori değeri

  const fetchProducts = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/products?page=${currentPage}&limit=${productsPerPage}`);
      if (resp && resp.status === 200) {
        const { pagination, products } = resp.data;
        const { totalPages } = pagination;

        const decodedProducts = products.map((product) => {
          const base64Image = `data:image/jpeg;base64,${product.productImage}`;
          return {
            ...product,
            productImage: base64Image
          };
        });

        props.setProducts(decodedProducts);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  const calculateTotalCalories = () => {
    let total = 0;

    // Tüm tarihlerdeki ürünlerin kalori değerlerini topla
    calorieInfoList.forEach((calorieInfo) => {
      calorieInfo.products.forEach((product) => {
        total += parseInt(product.calorieValue);
      });
    });

    return total.toString();
  };

  const fetchCalorieInfoList = async () => {
    try {
      const resp = await axios.get(`http://localhost:3001/calorieInfo`);
      if (resp && resp.status === 200) {
        props.setCalorieInfoList(resp.data);
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [currentPage]);

  useEffect(() => {
    fetchCalorieInfoList();
  }, []);

  const handleAddToList = async (productId, calorieValue) => {
    const currentDate = new Date().toISOString().slice(0, 10);

    try {
      const selectedProduct = allProducts.find((product) => product.productId === productId);
      console.log(selectedProduct, "selectedProduct")
      if (selectedProduct) {
        const {
          productId,
          productName,
          description,
          productImage,
          proteinValue,
          carbohydrateValue,
          oilValue,
          glutenValue,
          ketogenicDiet,
          glutenFree,
          saltFree,
          calorieValue
        } = selectedProduct;

        const resp = await axios.post(`http://localhost:3001/addList`, {
          calorieListId: "",
          products: [
            {
              productId: productId,
              productName: productName,
              description: description,
              productImage: "",
              proteinValue: proteinValue,
              carbohydrateValue: carbohydrateValue,
              oilValue: oilValue,
              glutenValue: glutenValue,
              ketogenicDiet: ketogenicDiet,
              glutenFree: glutenFree,
              saltFree: glutenFree,
              calorieValue: calorieValue
            }
          ],
          totalCalorie: calorieValue,
          createDate: currentDate
        });
        createCalorieInfo(resp.data);

        // Toplam kalori değerini güncelle
        setTotalCalories(parseInt(totalCalories) + parseInt(calorieValue));
      } else {
        console.log('Product not found');
      }
    } catch (error) {
      console.log('Error:', error.response);
    }
  };

  const renderProductList = calorieInfoList && calorieInfoList.map((calorieInfo) => {
    const { products } = calorieInfo;

    return products && products.map((product) => {
      const {
        productId,
        productName,
        description,
        productImage,
        carbohydrateValue,
        glutenFree,
        glutenValue,
        ketogenicDiet,
        oilValue,
        proteinValue,
        saltFree,
        calorieValue
      } = product;

      return (
        <Paper elevation={10} style={{ marginTop: '50px', marginBottom: '80px', padding: '20px', position: 'relative' }}>
          <Grid container spacing={2}>
            {/* Ürün görseli */}
            <Grid item xs={12} sm={4}>
              <img
                src={productImage}
                alt={description}
                align="center"
                style={{ width: '100%', height: 'auto' }}
              />
            </Grid>
            {/* Ürün bilgileri */}
            <Grid item xs={12} sm={8}>
              <Typography variant="h4" component="h2" style={{ marginBottom: '10px' }}>
                {productName}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                {description}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Protein: {proteinValue}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Hidrat: {carbohydrateValue}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Yağ: {oilValue}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Gluten Değeri: {glutenValue}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Gluten Free: {glutenFree}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Ketogenic Diet: {ketogenicDiet}
              </Typography>
              <Typography variant="body1" component="p" style={{ marginBottom: '5px' }}>
                Salt Free: {saltFree}
              </Typography>
              <Typography variant="body1" component="p">
                Kalori: {calorieValue}
              </Typography>
              <Button
                variant="contained"
                color="secondary"
                size="small"
                style={{ position: 'absolute', bottom: '10px', right: '10px' }}
                onClick={() => handleAddToList(productId, calorieValue)}
              >
                Bugünün Listesine Ekle
              </Button>
            </Grid>
          </Grid>
        </Paper>
      );
    });
  });

  return (
    <div>
      <h1 style={{ color: '#9c27b0' }}>Calorie Info Page</h1>
      <Paper elevation={3} style={{ padding: '20px', marginBottom: '20px' }}>
        <Typography variant="h5" color='secondary' gutterBottom>
          Add Food
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              select
              label="Select Product"
              fullWidth
              variant="outlined"
              value={selectedProduct}
              onChange={(e) => setSelectedProduct(e.target.value)}
            >
              {allProducts && allProducts.map((product) => (
                <MenuItem key={product.productId} value={product.productId}>
                  {product.productName}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button
              variant="contained"
              color="secondary"
              fullWidth
              onClick={() => handleAddToList(selectedProduct)}
            >
              Add to List
            </Button>
          </Grid>
        </Grid>
      </Paper>
      <Typography variant="h5" color='secondary' gutterBottom>
        Total Calories: {calculateTotalCalories()}
      </Typography>
      {renderProductList}
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <Pagination
          count={totalPages}
          page={currentPage}
          siblingCount={1}
          boundaryCount={1}
          variant="outlined"
          shape="rounded"
          onChange={(event, value) => setCurrentPage(value)}
        />
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    allProducts: state.product.allProducts,
    calorieInfoList: state.product.calorieInfoList
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    createCalorieInfo: (calorieInfo) => dispatch(createCalorieInfo(calorieInfo)),
    setCalorieInfoList: (calorieInfoList) => dispatch(setCalorieInfoList(calorieInfoList)),
    setProducts: (products) => dispatch(setProducts(products))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CalorieInfoPage);
