import React, { useEffect } from 'react'
import { connect } from "react-redux";
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux"
import { selectedProducts, removeSelectedProductReducer } from '../redux/actions/productActions';
import { Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import '@fontsource/roboto';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';


const ProductDetail = (props) => {

    const { allProducts } = props;
    const { productId } = useParams();
    const dispatch = useDispatch();
    const { productName, description, productImage, carbohydrateValue, glutenFree, glutenValue, ketogenicDiet, oilValue, proteinValue, saltFree, calorieValue } = product;

    console.log(allProducts)


    const fetchProctDetail = async () => {
        try {
            const resp = await axios.get(`localhost:3001/products/${productId}`);
            if (resp && resp.status === 200) {
                const decodedProducts = resp.data.map((product) => {
                    const base64Image = `data:image/jpeg;base64,${product.productImage}`;
                    return {
                        ...product,
                        productImage: base64Image
                    };
                });


                dispatch(setProducts(decodedProducts));
            }
        } catch (error) {
            console.log("Error:", error);
        }
    };



    useEffect(() => {
        if (productId && productId !== "") fetchProctDetail();
        return () => {
            dispatch(removeSelectedProductReducer())
        }
    }, [productId])



    return (
        <div>
            <h1>Product Detail</h1>
            <Paper elevation={10} style={{ marginTop: "50px", marginBottom: "80px", height: "950px" }} >
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={4}>
                        <img src={productImage} alt={title} aling="center" style={{ width: "300px", height: "350px", marginBottom: "50px", marginLeft: "10px" }}></img>
                    </Grid>
                    <Grid item xs={12} sm={4} style={{ color: "#9c27b0" }}>
                        <Typography variant="h4" component="h2">{productName}</Typography>
                        <Typography variant="h4" component="h2">{proteinValue}</Typography>
                        <Typography variant="h4" component="h2">{carbohydrateValue}</Typography>
                        <Typography variant="h4" component="h2">{oilValue}</Typography>
                        <Typography variant="h4" component="h2">{glutenValue}</Typography>
                        <Typography variant="h4" component="h2">{glutenFree}</Typography>
                        <Typography variant="h4" component="h2">{ketogenicDiet}</Typography>
                        <Typography variant="h4" component="h2">{saltFree}</Typography>
                        <Typography variant="h4" component="h2">{calorieValue}</Typography>
                    </Grid>
                </Grid>
            </Paper >
        </div>
    )
}


const mapStateToProps = (state) => ({
    allProducts: state.allProducts.products
});

const mapDispatchToProps = () => ({});

export default connect(mapStateToProps, mapDispatchToProps)(ProductDetail);
