import React from 'react'
import {useSelector} from 'react-redux'
import Grid from '@mui/material/Grid';
import { Paper, Typography } from '@mui/material';


const Product = () => {

    const products = useSelector((state) => state.allProducts.products);
    const renderList = products.map((product) => {

        const {id, title , image, price, category} = product;

        return(
            <Grid style={{border:"ridge",borderRadius: "50px", width: "300px", height:"500px", marginLeft: "50px"}}>
            <Typography style={{marginTop: "400px", marginLeft: "20px", fontSize:"30px"}}>{title},
            <img src={image} alt= {title}></img>
                   </Typography>
                   <Typography style={{marginTop: "400px", marginLeft: "20px", fontSize:"30px"}}>$ { price}
                   </Typography>
                   <Typography style={{marginTop: "400px", marginLeft: "20px", fontSize:"30px"}}>{category}
                   </Typography>
           </Grid>
        );
    })


return <>renderList</>;

}


export default Product
